mapper.select:
select announcementid, member_id memberid, title, content, hit,
case when date_format(regtime, '%Y%m%d') = date_format(now(), '%Y%m%d')
then date_format(regtime, '%H:%i:%s')
else date_format(regtime, '%y.%m.%d')
end regtime
from announcement


        where title like concat('%', #{word}, '%')


        where ${key} = #{word}


order by announcementid desc
limit #{start}, #{spp}

select count(announcementid) from announcement
where title like concat('%', #{word}, '%')
where ${key} = #{word}

select announcementid, member_id memberid, title, content, hit, regtime
from announcement
where announcementid = #{announcementid}

mapper.update:
update announcement
set hit = hit + 1
where announcementid = #{announcementid}

update announcement
set title = #{title}, content = #{content}
where announcementid = #{announcementid}
