mapper.select:
select id,name, userId, email
from member
where userId = #{userid} and password = #{userpwd}

mapper.update:
update member
set name = #{name},
    email = #{email},
    address = #{address},
    number = #{number}
where userId = #{userId};

mapper.select:
select token
from member
where userId = #{userid}

mapper.update:
update member
set token = #{token}
where userId = #{userid}

update member
set token = #{token, jdbcType=VARCHAR}
where userId = #{userid}
