mapper.select:
select review_id reviewId, member_id memberId,aptCode, content, date_format(created_date,'%Y-%m-%d') createdDate, likes,residenceFloor,residenceType,residenceYear,stars,ulikes
from review
where aptCode=#{aptCode}

mapper.update:
update review set residenceType=#{residenceType},
                  residenceYear=#{residenceYear},
                  residenceFloor=#{residenceFloor},
                  content=#{content},
                  stars=#{stars}
where review_id=#{reviewId};
