mapper.select:
select review_id reviewId, r.member_id memberId,name,aptCode, content, date_format(created_date,'%Y-%m-%d') createdDate, likes,residenceFloor,residenceType,residenceYear,stars,ulikes
from review r
inner join member m
on r.member_id = m.id
where aptCode=#{aptCode}
order by createdDate desc;

mapper.update:
update review set residenceType=#{residenceType},
                  residenceYear=#{residenceYear},
                  residenceFloor=#{residenceFloor},
                  content=#{content},
                  stars=#{stars}
where review_id=#{reviewId};
